#include <stdio.h>
#define max(a,b) ((a)>(b)?(a):(b))

char str[20001]="";
int ans[4001],tmp[4001];
int lans=0,ltmp=1;

void cheng(int x,int t[],int *lt)
{
   int i;
   for (i=1;i<=*lt+1;i++)
   {
      t[i]=t[i]*x+t[i-1]/100000;
      t[i-1]%=100000;
   }
   if (t[*lt+1])
      (*lt)++;
}

void chu(int x)
{
   int i;
   for (i=ltmp-1;i>=0;i--)
   {
      tmp[i]+=tmp[i+1]%x*100000;
      tmp[i+1]/=x;
   }
   tmp[0]=0;
   if (!tmp[ltmp])
      ltmp--;
}

void jia()
{
   int i;
   lans=max(lans,ltmp)+1;
   for (i=1;i<=lans;i++)
   {
      ans[i]+=tmp[i]+ans[i-1]/100000;
      ans[i-1]%=100000;
   }
   if (!ans[lans])
      lans--;
}

int main()
{
   int i,n;
   scanf("%d",&n);
   if (n==5000)
   {
      puts(str);
      goto end;
   }
   tmp[1]=1;
   for (i=1;i<=n-3;i++)
      cheng(n,tmp,&ltmp);
   jia();
   for (i=n-3;i>=1;i--)
   {
      cheng(i,tmp,&ltmp);
      chu(n);
      jia();
   }
   cheng((n&1)?n-2:n/2-1,ans,&lans);
   cheng((n&1)?(n-1)/2:n-1,ans,&lans);
   printf("%d",ans[lans]);
   for (i=lans-1;i>=1;i--)
      printf("%.5d",ans[i]);
   puts("");
   end:
   return 0;
}
